  const clienteRaw = item.json.meta?.telefoneCliente || '';
  const empresaRaw = item.json.meta?.telefoneEmpresa || '';

  const telefoneCliente = clienteRaw.split('@')[0];
  const telefoneEmpresa = empresaRaw.split('@')[0];

  return {
