={{ (() => {
  let messages = '';
  let quoted = '';
  let empilha = '';
  let transcricaoImagem = '';

  // Primeira parte: join de mensagens
  try {
    messages = $json.message.map(buffer => JSON.parse(buffer).message).join('\n');
  } catch (e) {
    messages = '';
  }

  // Segunda parte: conteúdo citado (quoted)
  try {
    quoted = $('camposIniciais1').first().json.content?.quoted || '';
  } catch (e) {
    quoted = '';
  }

  // Terceira parte: verificar empilhaÁudio se foi executado
  try {
    empilha = $node["empilhaÁudio2"]?.json?.text || '';
  } catch (e) {
    empilha = '';
  }

  // Quarta parte: buscar transcrição da imagem
  try {
    transcricaoImagem = $('transcreve imagem1').first().json.content || '';
  } catch (e) {
    transcricaoImagem = '';
  }

  // Juntar tudo e remover links do WhatsApp do resultado final
  let resultado = [messages, quoted, empilha, transcricaoImagem].filter(Boolean).join('\n\n');

  // Remove qualquer link do WhatsApp do resultado final
  resultado = resultado.replace(/https:\/\/mmg\.whatsapp\.net\/[^\s\n]+/g, '').trim();

  // Remove múltiplas quebras de linha consecutivas que podem ter ficado após remover o link
  resultado = resultado.replace(/\n\n+/g, '\n\n').trim();

  return resultado;
})() }}

