};

// Pega os dados dos nós anteriores
const camposIniciais = items[0].json;
const respostaIA = $input.item.json.output || $input.item.json.text || "";

// Função para limpar e escapar texto
function limparTexto(texto) {
  if (!texto) return "";

  return String(texto)
    .replace(/\\/g, '\\\\')     // Escapa barras invertidas primeiro
    .replace(/"/g, '\\"')       // Escapa aspas duplas
    .replace(/\n/g, '\\n')      // Escapa quebras de linha
    .replace(/\r/g, '\\r')      // Escapa retorno de carro
    .replace(/\t/g, '\\t');     // Escapa tabulações
}

// Monta o objeto com os dados limpos
const mensagem = {
  nome: limparTexto(camposIniciais.meta?.nomeCliente || "Higor Leite"),
  telefone: String(camposIniciais.meta?.telefoneCliente || "5511994151865"),
  type: "message",
  text: "",
  body: limparTexto(respostaIA)
};

// Retorna o JSON limpo e seguro
return [{
  json: mensagem
}];

