={{ (() => {
  try {
    // Caminho 1: message
    if ($json.message?.length) {
      return $json.message.map(buffer => JSON.parse(buffer).message).join('\n');
    }

    // Caminho 2: camposIniciais
    const quoted = $item(0).$node["camposIniciais"]?.json?.content?.quoted;
    if (quoted) {
      return quoted;
    }

    // Caminho 3: empilhaÁudio1 — só tenta acessar se o node foi executado
    if ($workflow.nodes["empilhaÁudio1"]) {
      const empilha = $item(0).$node["empilhaÁudio1"]?.json?.text;
      if (empilha) {
        return empilha;
      }
    }

    return "";
  } catch (error) {
    return "";
  }
})() }}
