const items = $input.all();
const resultados = [];

for (const item of items) {
  const body = item.json.body;
  let telefone = body["Nome e Telefone (1)"];
  const nomeCompleto = body["Nome e Telefone"];

  if (telefone) {
    if (!telefone.startsWith("55")) {
      telefone = "55" + telefone;
    }
  }

  if (nomeCompleto) {
    const partesNome = nomeCompleto.trim().split(" ");
    const nome = partesNome[0];
    const sobrenome = partesNome.slice(1).join(" ");

    resultados.push({
      nome: nome,
      sobrenome: sobrenome,
      telefone: telefone,
    });
  } else {
    resultados.push({ telefone: telefone });
  }
}

return resultados;
