const inputData = Array.isArray($json) ? $json[0]?.body || $json[0] : $json.body || $json;

// FunÃ§Ã£o para buscar um campo independentemente do nome
function encontrarCampo(possibilidades) {
    for (const key of possibilidades) {
        const encontrado = Object.keys(inputData).find(k => k.toLowerCase() === key.toLowerCase());
        if (encontrado && inputData[encontrado]) return inputData[encontrado];
    }
    return "";
}

// FunÃ§Ã£o para formatar o nome
function formatarNome(nome) {
    if (!nome) return { primeiro_nome: "", sobrenome: "" };
    const partes = nome.trim().split(" ");
    const primeiro_nome = partes.shift();
    const sobrenome = partes.length > 0 ? partes.join(" ") : "";
    return { primeiro_nome, sobrenome };
}

// FunÃ§Ã£o para formatar o telefone mantendo o DDD correto
function formatarTelefone(telefone) {
    if (!telefone) return "";
    let numero = telefone.replace(/\D/g, "");
    return numero.length >= 10 ? numero : "";
}

// FunÃ§Ã£o para extrair data e hora se vierem juntos
function extrairDataHora(completa) {
    if (!completa) return { data: "", horario: "" };
    const partes = completa.split(" ");
    const data = partes[0] || "";
    const horario = (partes[1] || "").slice(0, 5); // pega apenas HH:mm
    return { data, horario };
}

// FunÃ§Ã£o para formatar data e hora final
function formatarDataHora(data, horario) {
    if (!data || !horario) return "";
    return `${data} ${horario}:00`;
}

// Obtendo os valores dinamicamente
const nomeCompleto = encontrarCampo(["Nome", "name_customer"]);
const telefone = encontrarCampo(["telefone", "Telefone", "WhatsApp"]);
const email = encontrarCampo(["E-mail", "E_mail"]);
const dataHoraCompleta = encontrarCampo(["Data_da_conversao", "data", "Data"]);

const { data, horario } = extrairDataHora(dataHoraCompleta);

// Processando os dados
const { primeiro_nome, sobrenome } = formatarNome(nomeCompleto);
const telefoneFormatado = formatarTelefone(telefone);
const emailFormatado = email ? email.toLowerCase() : "";
const dataHoraFormatada = formatarDataHora(data, horario);

// Retorno formatado
return [
    {
        primeiro_nome,
        sobrenome,
        telefone: telefoneFormatado,
        email: emailFormatado,
        data_hora: dataHoraFormatada
    }
];
