function formatarTelefone(telefone) {
    let numero = telefone.replace(/\D/g, ""); // Remove tudo que n√£o for n√∫mero
    if (numero.length < 10) return null; // Retorna null se o n√∫mero for inv√°lido

    // Verifica se o n√∫mero j√° come√ßa com "55"
    if (!numero.startsWith("55")) {
        numero = "55" + numero;
    }

    return numero;
}

