// Pegando os dados do body do webhook
const data = $json["body"]["data"];

// Função para formatar o email em letras minúsculas
const email = data.buyer.email.toLowerCase();

// Função para separar o nome e sobrenome
const nomeCompleto = data.buyer.name.split(" ");
const nome = nomeCompleto[0]; // Primeiro nome
const sobrenome = nomeCompleto.slice(1).join(" "); // O restante é o sobrenome

// Função para remover espaços e caracteres do número de telefone, deixando apenas os números
let phoneNumber = data.buyer.checkout_phone.replace(/\D/g, '');

// Adicionando "55" na frente do telefone, caso não tenha
if (!phoneNumber.startsWith("55")) {
    phoneNumber = "55" + phoneNumber;
}

// Dados da compra
const produtoNome = data.product.name;
const produtoId = data.product.id;
const produtoUcode = data.product.ucode;
const preçoTotal = data.purchase.price.value;
const statusCompra = data.purchase.status;
const transactionId = data.purchase.transaction;

// Dados do pagamento
const metodoPagamento = data.purchase.payment.type;
const numeroParcelas = data.purchase.payment.installments_number;

// Dados do endereço do comprador
const endereco = data.buyer.address;
const enderecoCompleto = `${endereco.address}, ${endereco.number} - ${endereco.neighborhood}, ${endereco.city} - ${endereco.state}, ${endereco.zipcode}`;

// Dados das comissões
const comissoes = data.commissions.map(commission => ({
    fonte: commission.source,
    valor: commission.value,
    moeda: commission.currency_value
}));

// Dados do produtor
const produtorNome = data.producer.name;

// UTM's
const utm_source = data.utm_source || '';
const utm_campaign = data.utm_campaign || '';
const utm_medium = data.utm_medium || '';
const utm_content = data.utm_content || '';
const utm_term = data.utm_term || '';

// Retornando todos os dados organizados
return {
  email: email,
  nome: nome,
  sobrenome: sobrenome,
  telefone: phoneNumber,
  produto: {
    nome: produtoNome,
    id: produtoId,
    ucode: produtoUcode
  },
  compra: {
    preco_total: preçoTotal,
    status: statusCompra,
    transaction_id: transactionId
  },
  pagamento: {
    metodo: metodoPagamento,
    parcelas: numeroParcelas
  },
  endereco: enderecoCompleto,
  comissoes: comissoes,
  produtor: produtorNome,
  utm_source: utm_source,
  utm_campaign: utm_campaign,
  utm_medium: utm_medium,
  utm_content: utm_content,
  utm_term: utm_term
};
